import Image from 'next/image';
/**
 * Advanced image optimization utilities
 * Implements responsive images, lazy loading, and format optimization
 */

interface OptimizedImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  className?: string;
  sizes?: string;
}

export function OptimizedImage({
  src,
  alt,
  width,
  height,
  priority = false,
  className = '',
  sizes = '(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw'
}: OptimizedImageProps) {
  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      priority={priority}
      className={className}
      sizes={sizes}
      quality={85}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  );
}

/**
 * Generate responsive image sizes for different breakpoints
 */
export function generateImageSizes(
  baseWidth: number,
  breakpoints: Record<string, number> = {
    mobile: 640,
    tablet: 768,
    laptop: 1024,
    desktop: 1280
  }
): string {
  const sizeEntries = Object.entries(breakpoints).map(([device, width]) => {
    const calculatedWidth = Math.min(baseWidth, width);
    return `(max-width: ${width}px) ${calculatedWidth}px`;
  });
  
  return [...sizeEntries, `${baseWidth}px`].join(', ');
}